import React, { useState } from "react";
import AddItem from "./components/AddItem";
import List from "./components/List";
import Context from "./context";

function App() {
  const [todos, setTodos] = React.useState([
    { id: 1, completed: true, title: "Look to the next action" },
    { id: 2, completed: false, title: "Make done previous and this items" },
    { id: 3, completed: false, title: "To be proud of yourself" },
  ]);

  function toggleToDo(id) {
    setTodos(
      todos.map((todo) => {
        if (todo.id == id) todo.completed = !todo.completed;
        return todo;
      })
    );
  }

  function removeItem(id) {
    setTodos(todos.filter((item) => item.id !== id));
  }

  function addItem(title) {
    setTodos(
      todos.push()
    );
  }

  return (
    <Context.Provider value={{ removeItem }}>
      <div className="wrapper">
        <h1>2DoList</h1>
        <AddItem className="input" createItem={addItem} />
        {todos.length ? (
          <List todos={todos} toggle={toggleToDo} />
        ) : (
          <span>nothing to do</span>
        )}
      </div>
    </Context.Provider>
  );
}

export default App;
