import React from "react";

function Goal(props) {
  const classes = ["list__item"];
  if (props.goal.comleted) {
    classes.push("completed");
  }
  function checkHandler(event) {
    props.checkGoal(props.goal.id);
  }
  function removeHandler(event) {
    props.removeGoal(props.goal.id);
  }

  return (
    <li className={classes.join(" ")} onDoubleClick={checkHandler}>
      <div className="list__title">
        <p className="list__number">{props.index + 1}.</p>
        <p className="list">{props.goal.value}</p>
      </div>
      <div className="list__buttons">
        <button className="edit-button unselectable">✎</button>
        <button className="check-button unselectable" onClick={checkHandler}>
          ✔
        </button>
        <button className="delete-button unselectable" onClick={removeHandler}>
          ✘
        </button>
      </div>
    </li>
  );
}

export default Goal;
