import React, { useState } from "react";
import GoalsList from "./components/GoalsList";
import Input from "./components/Input";
import Context from "./context";

function App() {
  const [goals, setGoals] = useState([
    { id: 1, value: "Learn HTML", comleted: false },
    { id: 2, value: "Learn CSS", comleted: false },
    { id: 3, value: "Learn JS", comleted: false },
    { id: 4, value: "Learn React", comleted: false },
  ]);

  let generateId = () => {
    let s4 = () => {
      return Math.floor((1 + Math.random()) * 0x10000)
        .toString(16)
        .substring(1);
    };
    return s4() + s4() + "-" + s4();
  };

  function handleValue(value) {
    setGoals(
      goals.concat([
        {
          id: generateId(),
          value: { value },
          comleted: "false",
        },
      ])
    );
  }
  console.log(goals);
  return (
    <div className="wrapper">
      <header className="header">
        <h1 className="header__title">Goals</h1>
      </header>
      <main className="main">
        <div className="goals container">
          <Input onBlur={handleValue} />
          <GoalsList goals={goals} />
        </div>
      </main>
    </div>
  );
}

export default App;
